
const delay=400
window.addEventListener('DOMContentLoaded',function(){const menu=document.querySelector('.header__menu')
const header=document.querySelector('.header')
let lock=false
const burger=document.querySelector('.header__burger')
burger.addEventListener('click',function(){if(lock)return
lock=true
toggleMenu()
setTimeout(()=>{lock=false},delay)})
function toggleMenu(){burger.classList.toggle('active')
menu.classList.toggle('active')
header.classList.toggle('active')
lockBody()}
fixHeader()
function fixHeader(){if(document.body.scrollTop>10&&!header.classList.contains('fix')){header.classList.add('fix')}
if(document.body.scrollTop<=0&&header.classList.contains('fix')){header.classList.remove('fix')}}
document.body.addEventListener('scroll',fixHeader)
function lockBody(){const scrollWidth=window.innerWidth-document.body.clientWidth
document.body.classList.toggle('lock')
document.body.style.paddingRight=scrollWidth+'px'}
const inputs=document.querySelectorAll('.contact-us__input')
if(inputs.length>0){for(let index=0;index<inputs.length;index++){const input=inputs[index]
input.addEventListener('input',function(e){if(e.target.value.length>0&&!e.target.classList.contains('fix-placeholder')){e.target.classList.add('fix-placeholder')}
if(e.target.value.length===0&&e.target.classList.contains('fix-placeholder')){e.target.classList.remove('fix-placeholder')}})}}})